.heroCarousel-content {
    position: absolute;
    padding: 0;
    width: 100%;
}

.outer .inner {
    font-size: 1rem;
    font-weight: 700;
    text-shadow: -2px 2px 8px black;
}

.heroCarousel {
    margin-bottom: 0;
